function query(id){return document.querySelector(id)}
function queryAll(id){return document.querySelectorAll(id)}
function block(id){query(id).style.display="block"}
function none(id){query(id).style.display="none"}
function addClass(idElement,addClass){query(idElement).classList.add(addClass)}
function removeClass(idElement,rmClass){query(idElement).classList.remove(rmClass)}
function submitForm(formId){query(formId).submit()}
function insertHTML(id,html){query(id).innerHTML=html}
function focusInput(idInput){query(idInput).focus()}
function getValueRange(id,idDisplay){query(idDisplay).innerHTML=parseFloat(query(id).value)}
function print(){window.print()}
function back(){window.history.go(-1)}
function next(){window.history.go(1)}
function reload(){window.history.go(0)}
function url(){return window.location.href}
function urlHost(){return window.location.hostname}
function urlHash(){return window.location.hash}
function urlProtocol(){return window.location.protocol}
function classScroll(idElement){let element=query(idElement);let insertClass=element.getAttribute("data-addclass");let numPx=element.getAttribute("data-scrollpx");let top=window.pageYOffset||document.documentElement.scrollTop;if(top>numPx){addClass(idElement,insertClass)}else{removeClass(idElement,insertClass)}}
function interval(time,action){setInterval(action,time)}
function addText(idElement,text){let area=query(idElement);let cursorPosition=area.selectionStart;let front=(area.value).substring(0,cursorPosition);let back=(area.value).substring(cursorPosition,area.value.length);area.value=front+text+back;cursorPosition=cursorPosition+text.length;area.selectionStart=cursorPosition;area.selectionEnd=cursorPosition;area.focus()}
function saveText(nameElement,idElement){let textscreen=query(idElement).value;window.localStorage.setItem(nameElement,textscreen)}
function recoverText(nameElement,activeReturn='',idElement=''){let text=window.localStorage.getItem(nameElement);if(text==null){let text=''}
if(activeReturn==''){return text}else{addText(idElement,text)}}
function notify(title,icon,text,url){Notification.requestPermission(function(){let notification=new Notification(title,{icon:icon,body:text});notification.onclick=function(){window.open(url)}})}
function randomNumbers(min,max){return Math.floor(Math.random()*(max-min+1))}
function scrollTop(px=0){document.body.scrollTop=px;document.documentElement.scrollTop=px}
